import { StatusBar } from 'expo-status-bar';
import React, {Component, useState} from 'react';
import { ActivityIndicator, Button, Clipboard, FlatList, Share, StyleSheet, Text, TextInput, TouchableOpacity, Image, View } from 'react-native';
import { WebView } from 'react-native-webview';

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F5FCFF',
    alignItems: 'center',
    justifyContent: 'center',
  },
  common: {
    paddingTop: 50
  },
  logo: {
    width: 300,
    height:  200
  },
  text_input: {
    width: "100%",
    borderBottomWidth: 1,
    borderBottomColor: "#ccc"
  },
  text_input_wrapper: {
    backgroundColor: '#F5FCFF'
  },
});

export default function App() {

  const [text, setText] = useState("");
  const [input, setInput] = useState("");

  const data = [
    {str: 'Rei Mizobuchi', key: 'boy'},
    {str: 'Nanami Tomatsu', key: 'girl'}
  ]

  function pbcopy() {
    ClipBoard.setString(input);
  }

  async function getpb() {
    const text = await ClipBoard.getString();
    setText(text);
  }

  return (
    <View style={styles.container}>
      <TouchableOpacity onPress={() => console.warn('pressed!')}>
        <Image style={styles.logo}source={require('./assets/react-native-logo.png')}/>
      </TouchableOpacity>
      <TextInput style={{width: '100%', textAlign: 'center', borderBottomWidth: 1, borderColor: '#ccc'}} value={input} onChangeText={input => setInput(input)}/>
      <Button onPress={() => {pbcopy()}} title="save to ClipBoard"/>
      <Text>{text}</Text>
      <Button onPress={() => {getpb()}} title="get from ClipBoard"/>
    </View>
  );
}
