import { StatusBar } from 'expo-status-bar';
import React, {Component, useState} from 'react';
import { ActivityIndicator, FlatList, StyleSheet, Text, TextInput, TouchableOpacity, Image, View } from 'react-native';

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  common: {
    paddingTop: 50
  },
  logo: {
    width: 300,
    height:  200
  },
  text_input: {
    width: "100%",
    borderBottomWidth: 1,
    borderBottomColor: "#ccc"
  },
  text_input_wrapper: {
    backgroundColor: '#F5FCFF'
  },
});

export default function App() {

  const [text, setText] = useState("");

  const data = [
    {str: 'Rei Mizobuchi', key: 'boy'},
    {str: 'Nanami Tomatsu', key: 'girl'}
  ]

  return (
    <View style={styles.container}>
      <TouchableOpacity onPress={() => console.warn('pressed!')}>
        <Image style={styles.logo}source={require('./assets/react-native-logo.png')}/>
      </TouchableOpacity>
      <View style={styles.common}>
        <ActivityIndicator/>
      </View>
      <FlatList data={data} renderItem={({item}) => {
        return(
          <Text key={item.key}>{item.str}</Text>
        )
      }}
      />
      <View style={styles.text_input_wrapper}>
        <TextInput onChangeText={text => setText(text)} value={text}/>
      </View>
    </View>
  );
}
